        });
        On<OrderShipped>(@event => {
            Update(@event OrderId, summary => {
                summary Status = "Shipped";
                summary ShippedAt = @event ShippedAt;
            });
        });
        On<OrderCancelled>(@event => {
            Update(@event OrderId, summary => summary Status = "Cancelled");
        });
    }
}
